import React from "react";
import { useNavigate } from "react-router-dom";
import User from '../model/user';
import UserService from '../service/UserService';

function AddUsers() {
    const service = new UserService();
    const navigate = useNavigate();
    const [state, changeState] = React.useState({ user: new User() });
    const [error, setError] = React.useState({
        userNameError: '',
        passwordError: '',
        userTypeError: '',
        emailError: '',
        mobileError: ''
    });

    React.useEffect(() => {
        if (sessionStorage.getItem('userType') === 'Customer') {
            alert('Unauthorised Access to Page.');
            navigate('/links');
        }
    }, [navigate]);

    const validateForm = () => {
        let err = {};
        let isError = false;

        // User name validation
        if (!state.user.userName) {
            err.userNameError = "User name is required.";
            isError = true;
        }

        // Password validation
        if (!state.user.password) {
            err.passwordError = "Password is required.";
            isError = true;
        }

        // User Type validation (only for Admin)
        if (sessionStorage.getItem('userType') === 'Admin' && !state.user.userType) {
            err.userTypeError = "User type is required.";
            isError = true;
        }

        // Email validation (must end with .com)
        if (!state.user.email) {
            err.emailError = "Email is required.";
            isError = true;
        } else if (!state.user.email.endsWith(".com")) {
            err.emailError = "Email must end with .com.";
            isError = true;
        }

        // Mobile Number validation (must be 10 digits)
        if (!state.user.mobileNumber) {
            err.mobileError = "Mobile number is required.";
            isError = true;
        } else if (!/^\d{10}$/.test(state.user.mobileNumber)) {
            err.mobileError = "Mobile number must be exactly 10 digits.";
            isError = true;
        }

        setError(err);
        return isError;
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        if (sessionStorage.getItem('userType') !== 'Admin') {
            state.user.userType = 'Customer';
        }

        if (!validateForm()) {
            service.addUser(state.user)
                .then(() => {
                    navigate("/login");
                })
                .catch(() => {
                    alert("Problem in adding user.");
                });
        }
    };

    return (
        <div className="container my-4">
            <style>
                {`
                    .form-container {
                        max-width: 500px;
                        margin: 0 auto;
                        padding: 30px;
                        border-radius: 10px;
                        background-color: #f9f9f9;
                        box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
                    }

                    .form-container input {
                        border-radius: 10px;
                        margin-bottom: 10px;
                    }

                    .form-container button {
                        width: 100%;
                        padding: 10px;
                        border-radius: 10px;
                        font-weight: bold;
                        font-size: 16px;
                    }

                    .form-container .badge {
                        font-size: 18px;
                    }

                    .text-danger {
                        font-size: 14px;
                        margin-top: 5px;
                    }

                    .fas, .far {
                        margin-right: 8px;
                    }
                `}
            </style>

            <form onSubmit={handleSubmit} className="form-container">
                <h2 className="text-center mb-4">
                    <span className="badge badge-dark px-4 py-2"><i className="fas fa-user-plus"></i> &nbsp;Register New User</span>
                </h2>

                <div className='form-group'>
                    <label><i className="fas fa-user"></i>&nbsp;User Name</label>
                    <div className="text-danger">{error.userNameError}</div>
                    <input className="form-control" type="text" id="userName" name="userName" placeholder="Enter user name"
                        value={state.user.userName}
                        onChange={(event) => changeState({ user: { ...state.user, userName: event.target.value } })}
                    />
                </div>

                <div className='form-group'>
                    <label><i className="fas fa-key"></i>&nbsp;Password</label>
                    <div className="text-danger">{error.passwordError}</div>
                    <input className="form-control" type="password" id="password" name="password" placeholder="Enter password"
                        value={state.user.password}
                        onChange={(event) => changeState({ user: { ...state.user, password: event.target.value } })}
                    />
                </div>

                {sessionStorage.getItem('userType') === 'Admin' && (
                    <div className='form-group'>
                        <label><i className="fas fa-user-cog"></i>&nbsp;User Type</label>
                        <div className="text-danger">{error.userTypeError}</div>
                        <input className="form-control" type="text" id="userType" name="userType" placeholder="Enter user type"
                            value={state.user.userType}
                            onChange={(event) => changeState({ user: { ...state.user, userType: event.target.value } })}
                        />
                    </div>
                )}

                <div className='form-group'>
                    <label><i className="far fa-envelope-open"></i>&nbsp;Email</label>
                    <div className="text-danger">{error.emailError}</div>
                    <input className="form-control" type="text" id="email" name="email" placeholder="Enter email"
                        value={state.user.email}
                        onChange={(event) => changeState({ user: { ...state.user, email: event.target.value } })}
                    />
                </div>

                <div className='form-group'>
                    <label><i className="fas fa-phone"></i>&nbsp;Mobile Number</label>
                    <div className="text-danger">{error.mobileError}</div>
                    <input className="form-control" type="text" id="mobileNumber" name="mobileNumber" placeholder="Enter mobile number"
                        value={state.user.mobileNumber}
                        onChange={(event) => changeState({ user: { ...state.user, mobileNumber: event.target.value } })}
                    />
                </div>

                <div className="text-center">
                    <button className="btn btn-success my-3" type="submit">
                        <i className="fas fa-user-plus"></i> &nbsp;Add User
                    </button>
                </div>
            </form>
        </div>
    );
}

export default AddUsers;
