import React from "react";
import { Link } from "react-router-dom";

function Links() {
    const cards = [
        {
            path: "/add-student",
            icon: "fas fa-user-plus",
            title: "Add Student",
            description: "Add a new student to the system."
        },
        {
            path: "/view-students",
            icon: "fas fa-users",
            title: "View Students",
            description: "See the list of all registered students."
        },
        {
            path: "/add-course",
            icon: "fas fa-book",
            title: "Add Course",
            description: "Add a new course to the curriculum."
        },
        {
            path: "/view-courses",
            icon: "fas fa-book-open",
            title: "View Courses",
            description: "Browse the available courses."
        },
        {
            path: "/add-schedule",
            icon: "fas fa-calendar-plus",
            title: "Add Schedule",
            description: "Schedule classes for courses."
        },
        {
            path: "/view-schedules",
            icon: "fas fa-calendar-alt",
            title: "View Schedules",
            description: "Check the existing class schedules."
        },
        {
            path: "/update-student",
            icon: "fas fa-user-edit",
            title: "Update Student",
            description: "Edit details of a student."
        },
        {
            path: "/delete-student",
            icon: "fas fa-user-times",
            title: "Delete Student",
            description: "Remove a student from the system."
        },
        {
            path: "/update-course",
            icon: "fas fa-edit",
            title: "Update Course",
            description: "Edit course details and syllabus."
        },
        {
            path: "/delete-course",
            icon: "fas fa-trash",
            title: "Delete Course",
            description: "Delete a course permanently."
        },
        {
            path: "/update-schedule",
            icon: "fas fa-calendar-check",
            title: "Update Schedule",
            description: "Modify scheduled classes."
        },
        {
            path: "/delete-schedule",
            icon: "fas fa-calendar-times",
            title: "Delete Schedule",
            description: "Delete a schedule from the database."
        }
    ];

    return (
        <div style={{ padding: "2rem" }}>
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
                    gap: "1.5rem"
                }}
            >
                {cards.map((card, index) => (
                    <Link to={card.path} key={index} style={{ textDecoration: "none" }}>
                        <div
                            style={{
                                border: "1px solid #ccc",
                                borderRadius: "12px",
                                padding: "1.5rem",
                                textAlign: "center",
                                transition: "transform 0.3s ease, box-shadow 0.3s ease",
                                height: "150px",
                                backgroundColor: "#fff",
                                boxShadow: "0 2px 8px rgba(0,0,0,0.05)"
                            }}
                            onMouseEnter={e => {
                                e.currentTarget.style.transform = 'scale(1.05)';
                                e.currentTarget.style.boxShadow = '0 8px 16px rgba(0,0,0,0.15)';
                            }}
                            onMouseLeave={e => {
                                e.currentTarget.style.transform = 'scale(1)';
                                e.currentTarget.style.boxShadow = '0 2px 8px rgba(0,0,0,0.05)';
                            }}
                        >
                            <h5 style={{ fontSize: "1.2rem", marginBottom: "1rem", color: "#333" }}>
                                <i className={card.icon} style={{ marginRight: "8px" }}></i>
                                {card.title}
                            </h5>
                            <p style={{ fontSize: "0.9rem", color: "#666" }}>{card.description}</p>
                        </div>
                    </Link>
                ))}
            </div>
        </div>
    );
}

export default Links;
