import React, { useEffect, useState } from "react";
import AirportService from "../service/AirportService";
import { useNavigate } from "react-router-dom";

function DeleteAirport() {
    const [airports, setAirports] = useState([]);
    const service = new AirportService();
    const navigate = useNavigate();

    useEffect(() => {
        if (sessionStorage.getItem('userId') == null) {
            alert('Unauthorized Access to Page.');
            navigate('/login');
        } else {
            service.getAllAirports()
                .then(response => {
                    setAirports(response.data);
                })
                .catch(() => {
                    alert("Error fetching airports.");
                });
        }
    }, []);

    const deleteAirport = (id) => {
        if (window.confirm("Are you sure you want to delete this airport?")) {
            service.deleteAirport(id)
                .then(() => {
                    setAirports(airports.filter(a => a.airportId !== id));
                })
                .catch(() => {
                    alert("Error deleting airport.");
                });
        }
    };

    return (
        <div className="container mt-4">
            <h2><span className="badge badge-danger px-4 py-2"><i className="fas fa-trash-alt"></i>&nbsp; Delete Airports</span></h2>
            <table className="table table-bordered mt-3">
                <thead className="thead-dark">
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Location</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {airports.length === 0 ? (
                        <tr>
                            <td colSpan="4" className="text-center">No airports found.</td>
                        </tr>
                    ) : (
                        airports.map((airport) => (
                            <tr key={airport.airportId}>
                                <td>{airport.airportId}</td>
                                <td>{airport.airportName}</td>
                                <td>{airport.airportLocation}</td>
                                <td>
                                    <button
                                        className="btn btn-danger btn-sm"
                                        onClick={() => deleteAirport(airport.airportId)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))
                    )}
                </tbody>
            </table>
        </div>
    );
}

export default DeleteAirport;
