import React from "react";
import { Link, useNavigate } from "react-router-dom";

function Links() {
    const navigate = useNavigate();

    React.useEffect(() => {
        if (sessionStorage.getItem('userType') !== 'Admin') {
            alert('Unauthorized Access to Page.');
            navigate('/login');
        }
    }, []);

    return (
        <div>
            <div className="jumbotron text-center mb-1" style={{ backgroundColor: "aliceblue", padding: "2rem", marginBottom: "1rem", borderRadius: "8px" }}>
                <h3 style={{ marginBottom: "0.5rem", fontSize: "2rem" }}><i className="fas fa-paper-plane mr-1"></i> Admin Tools</h3>
                <h5 style={{ fontSize: "1.2rem", color: "#555" }}>Manage flight and user data centrally with admin tools.</h5>
            </div>
            <div style={{
                display: "grid",
                gridTemplateColumns: "repeat(auto-fill, minmax(240px, 1fr))",
                gap: "1rem",
                padding: "1rem"
            }}>
                {/* Card 1: View Airports */}
                <Link className="card-item" to="/airports" style={{ textDecoration: "none" }}>
                    <div style={{
                        borderRadius: "10px",
                        overflow: "hidden",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "transform 0.3s ease, box-shadow 0.3s ease"
                    }} onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}>

                        <div style={{ padding: "1.5rem", textAlign: "center" }}>
                            <h5 style={{ fontSize: "1.1rem", marginBottom: "1rem" }}><i className="fas fa-map-marker-alt"></i> View airports</h5>
                            <p style={{ fontSize: "0.9rem", color: "#777" }}>View all the airports in the database.</p>
                        </div>
                    </div>
                </Link>

                {/* Card 2: Add Airports */}
                <Link className="card-item" to="/airports/add" style={{ textDecoration: "none" }}>
                    <div style={{
                        borderRadius: "10px",
                        overflow: "hidden",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "transform 0.3s ease, box-shadow 0.3s ease"
                    }} onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}>

                        <div style={{ padding: "1.5rem", textAlign: "center" }}>
                            <h5 style={{ fontSize: "1.1rem", marginBottom: "1rem" }}><i className="fas fa-map-marker-alt"></i> Add airports</h5>
                            <p style={{ fontSize: "0.9rem", color: "#777" }}>Add an airport to the database.</p>
                        </div>
                    </div>
                </Link>

                {/* Card 3: Delete Airports */}
                <Link className="card-item" to="/airports/delete" style={{ textDecoration: "none" }}>
                    <div style={{
                        borderRadius: "10px",
                        overflow: "hidden",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "transform 0.3s ease, box-shadow 0.3s ease"
                    }} onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}>

                        <div style={{ padding: "1.5rem", textAlign: "center" }}>
                            <h5 style={{ fontSize: "1.1rem", marginBottom: "1rem" }}><i className="fas fa-map-marker-alt"></i> Delete airports</h5>
                            <p style={{ fontSize: "0.9rem", color: "#777" }}>Delete airports in the database.</p>
                        </div>
                    </div>
                </Link>

                {/* Card 4: View Flights */}
                <Link className="card-item" to="/flights" style={{ textDecoration: "none" }}>
                    <div style={{
                        borderRadius: "10px",
                        overflow: "hidden",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "transform 0.3s ease, box-shadow 0.3s ease"
                    }} onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}>

                        <div style={{ padding: "1.5rem", textAlign: "center" }}>
                            <h5 style={{ fontSize: "1.1rem", marginBottom: "1rem" }}><i className="fas fa-plane"></i> View planes</h5>
                            <p style={{ fontSize: "0.9rem", color: "#777" }}>View all planes and their carriers in the database.</p>
                        </div>
                    </div>
                </Link>

                {/* Card 5: Add Flights */}
                <Link className="card-item" to="/flights/add" style={{ textDecoration: "none" }}>
                    <div style={{
                        borderRadius: "10px",
                        overflow: "hidden",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "transform 0.3s ease, box-shadow 0.3s ease"
                    }} onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}>

                        <div style={{ padding: "1.5rem", textAlign: "center" }}>
                            <h5 style={{ fontSize: "1.1rem", marginBottom: "1rem" }}><i className="fas fa-plane"></i> Add planes</h5>
                            <p style={{ fontSize: "0.9rem", color: "#777" }}>Add planes to the database.</p>
                        </div>
                    </div>
                </Link>

                {/* Card 6: Delete Flights */}
                <Link className="card-item" to="/flights/delete" style={{ textDecoration: "none" }}>
                    <div style={{
                        borderRadius: "10px",
                        overflow: "hidden",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "transform 0.3s ease, box-shadow 0.3s ease"
                    }} onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}>

                        <div style={{ padding: "1.5rem", textAlign: "center" }}>
                            <h5 style={{ fontSize: "1.1rem", marginBottom: "1rem" }}><i className="fas fa-plane"></i> Delete planes</h5>
                            <p style={{ fontSize: "0.9rem", color: "#777" }}>Delete planes from the database.</p>
                        </div>
                    </div>
                </Link>

                {/* Card 7: Add Schedule */}
                <Link className="card-item" to="/schedule/add" style={{ textDecoration: "none" }}>
                    <div style={{
                        borderRadius: "10px",
                        overflow: "hidden",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "transform 0.3s ease, box-shadow 0.3s ease"
                    }} onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}>

                        <div style={{ padding: "1.5rem", textAlign: "center" }}>
                            <h5 style={{ fontSize: "1.1rem", marginBottom: "1rem" }}><i className="fas fa-calendar"></i> Add schedule</h5>
                            <p style={{ fontSize: "0.9rem", color: "#777" }}>Add a new schedule to the database.</p>
                        </div>
                    </div>
                </Link>

                {/* Card 8: Delete Schedule */}
                <Link className="card-item" to="/schedule/delete" style={{ textDecoration: "none" }}>
                    <div style={{
                        borderRadius: "10px",
                        overflow: "hidden",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "transform 0.3s ease, box-shadow 0.3s ease"
                    }} onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'} onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}>

                        <div style={{ padding: "1.5rem", textAlign: "center" }}>
                            <h5 style={{ fontSize: "1.1rem", marginBottom: "1rem" }}><i className="fas fa-calendar"></i> Delete schedule</h5>
                            <p style={{ fontSize: "0.9rem", color: "#777" }}>Delete a schedule from the database.</p>
                        </div>
                    </div>
                </Link>
            </div>
        </div>
    );
}

export default Links;
